mapper.select:

SELECT	* 
  FROM MEMBER
 WHERE MEMBER_ID = #{member_id}
   AND MEMBER_PASSWORD = #{member_password}

mapper.insert:
INSERT INTO MEMBER (
	MEMBER_ID
	, MEMBER_PASSWORD
	, MEMBER_NAME
	, MEMBER_PHONE
	, MEMBER_EMAIL
	) VALUES (
	#{member_id}
	,#{member_password}
	,#{member_name}
	,#{member_phone}
	,#{member_email}
	)
